INSERT INTO products (name, description, picture, price, year, width, height, depth)
VALUES  ('Purpurové polstrované lehátko', 'Pohodlné štýlové lehátko na pohodový čas pri pozeraní televízora alebo čítaní', 'art-deco/armchair-purple.jpg', 475, 1938, 60, 150, 200),
        ('Kreslo papagájov s motívom lesa', 'Textilná pošívka prekvapí nezvyčajným vzorom z listom, v ktorom centre sa nachádza majestátny papagáj Rozela žltobruchá. Neoddeliteľná súčasť pracovne každého zanieteného prírodovedca', 'art-deco/armchair.jpg', 520, 1933, 65, 140, 80),
        ('Úžitková posteľ s nášivkou striebornej farby', 'Posteľ, ktorá poslúži svojmu účelu. Síce nie je úplne najpohdlnejšia na dnešné štandardy, ale zažijete s ňou ozaj dobový spánok', 'art-deco/bed.jpg', 265, 1928, 90, 50, 180),
        ('Dekoračná knižnica na skrinke', 'Potrebujete obzvlášniť miesnosť, kde je všetko až príliš pravidelné a ste zároveň milovníkom kníh. Prezentujte svoj vkus návštevám prostredníctvom nevšednej knižnice s červenými poličkami na celkovo čiernom podklade', 'art-deco/bookcase.jpg', 615, 1932, 120, 210, 70),
        ('Klubové zaoblené kožené kreslo', 'Zvučí vám v ušiach od rána do večera šanzón? Páči sa vám predstava cigárami zafajčených klubov 20. rokov? Kúsok tamojšieho kúzla vá pripomenie práve toto kreslo', 'art-deco/clubsessel-cognac.jpg', 770, 1919, 120, 130, 120),
        ('Kubické kreslo s vyvýšením sedením', 'Ak hľadáte sedenie na vážne a dôležité porady medzi štyrmi očami toto kreslo je ako stvorené pre vás.', 'art-deco/cubic-couch.jpg', 590, 1925, 130, 145, 120),
        ('Zelená stolička s opierkou na lakte', 'Pokiaľ nezostáva v kuchyni priestor na hosné sedenie pri hodovaní siahnite po tejto stoličke', 'art-deco/green-chair.jpg', 250, 1916, 50, 160, 50),
        ('Chrómový umelecký rám lehátka', 'Vynikajúci umelecký pochrómovaný kúsok sa so spravnou matracou môže vynímať aj medzi najvyberenejším nábytkom', 'art-deco/chair-by-le-corbusier-and-perriand-1927.jpg', 840, 1927, 40, 60, 150),
        ('Sada do obývačky na hranie spoločenských hier', 'Častý hráči hostujúci posedenia s priateľmi s prídu na svoju so zlacnenou sadou stoličiek a okrúhleho stolado obývačky', 'art-deco/living-room-set-chairs-table.jpg', 2300, 1935, 40, 60, 150),
        ('Malý drevený nočný stolík', 'Pred spaním naň odložíte všetky cenné drobnosti, ktoré potrebujete mať vždy nablízku', 'art-deco/small-table-1930.jpg', 310, 1930, 60, 50, 50),
        ('Stupňový umelecký stôl', 'Medzi priechodnú kuchyňu a jedáleň sa hodí dať stôl kde budete pre svoju rodinu či hostí vykladať hotové jedlo', 'art-deco/step-table.jpg', 465, 1944, 180, 220, 40),
        ('Konferenčný stôl', 'Pevný stôl, ktorý ustane až tie najtvrdšie rozhodnutia a vydží ešte dlhé roky', 'art-deco/table-1930.jpg', 670, 1930, 70, 150, 70),
        ('Priestranná stolička z dubového dreva s textilnými poťahom', 'Stoličkovým rozvalovačom a všetci hľadajúci niečo medzi stoličkou a kreslom sa hodí práve táto stolička', 'art-nouveau/armchair-oak-textile-1901.jpg', 435, 1901, 100, 140, 100),
        ('Vaňa', 'Obyčajná vaňa musí byť súčaťou každej kúpeľne. Táto si už svoje zažila, ale stále je nablýskaná ako nová, vďaka odbornej opatere reštaurátorov', 'art-nouveau/bathtub.jpg', 325, 1915, 70, 100, 180),
        ('Veľkolepá manželská posteľ', 'Koráb nocou a vašimi snami s veľkým pevným dreveným prečelím a ozdobnou vysokou oprierkou', 'art-nouveau/bed-1856.jpg', 1620, 1856, 210, 200, 190),
        ('Znížená posteľ v secesnom štýle', 'Ťažké rannej vstávanie sa tejto pohodlnej postele nekonná. Znížený profil znamená, že vaše pri prevaľovaní sa nebudú také nepríjemné ako z postelí vyššeiho rázu.', 'art-nouveau/bed-1905.jpg', 1286, 1905, 180, 190, 170),
        ('Posteľ vstavaná do skrine', 'Nie všetci si môžu dovoliť veľkú nehnuteľnosť, no zato si ju chcú štýlovo vybaviť. V noci posteľ, cez deň súčasť skrine, čím sa uvoľní v miestnosti znační priestor.', 'art-nouveau/bed-large-1884.jpg', 1800, 1884, 180, 200, 230),
        ('Bar s vyrývanými vzormi', 'Skrinka neoodeliteľne patriaca do obývačky. Úložisko pre pochutiny a prípadný aperitív', 'art-nouveau/buffet-1903.jpg', 1735, 1903, 150, 200, 50),
        ('Vypuklá trojpoličková vytrína', 'Na výstavu všetkých dôležitých vecí zo života priamo na dohľad z každého kúta miestnosti', 'art-nouveau/cabinet.jpg', 1380, 1890, 200, 180, 40),
        ('Drevná pohovka so zrkadlom', 'Strohejšie sedenie pre širšie osadenstvo.', 'art-nouveau/deewan-1850.jpg', 505, 1850, 350, 160, 50),
        ('Taburetka', 'Malé príručné sedenie, ktoré je vždy naporúdzi, keď inde nie je miesto ', 'art-nouveau/galle-tabouret.jpg', 110, 1900, 50, 50, 50),
        ('Pracovný stôl s odkladacím priestorom', 'Užitočné do pracovne, kde sa stále pracuje s papierovými spismi. Umožňuje zakomponovať stolný počítač v dobovom chasy.', 'art-nouveau/grand-curtius-desk-table.jpg', 850, 1875, 180, 140, 70),
        ('Šachový stolík', 'Partičky tejto logickej hry na dosah ruky s hracou plochou priamo naporúdzi', 'art-nouveau/chess-table-1915.jpg', 280, 1915, 80, 120, 80),
        ('Malé umývadlo so zlatistým kohútikom', 'Nevyhnutné vybavenie do stiesnených kúpelní, keď si proste len potrebujete umyť ruky', 'art-nouveau/sink.jpg', 370, 1899, 30, 40, 30),
        ('Praktický stôl', 'Keď potrebujete stolík bez okázalostí ...', 'art-nouveau/table-1890.jpg', 230, 1890, 190, 110, 70),
        ('Okrúhly čajový stolík s intarziami', 'Krásne zdobený stolík na čajové dýchanky, alebo len na odkladanie bežných vecí', 'art-nouveau/wood-inlays-table-1905.jpg', 408, 1905, 60, 90, 60),
        ('Mauserov nerezový okrúhly stôl', 'Do každej kuchyne alebo jedálne nerezového vzhľadu s veľmi jednoduchými až industrálnymi prvkami', 'bauhaus/1930-table-mauser.jpg', 110, 1930, 65, 150, 65),
        ('Breuer Freischwinger replika', 'Stolička na dlhé pracovné stretnutia s rukopisom známeho návrhára Breuera', 'bauhaus/breuer-freishwigner.jpg', 215, 1925, 60, 110, 70),
        ('Bauhaus umývadlo', 'Obyčajné umývadlo s nezvyčajným dobovým kohútikom. Kombinácia, ktorá zaujme', 'bauhaus/sink.jpg', 150, 1932, 55, 50, 30),
        ('Breuer stolička Wassily', 'Viacúčelový predmet. Ani stolička, ani stôl, ale niečo medzitým. Žije si oba tieto životy zároveň a vie sa meniť medzi nimi. Sami musíte posúdiť, či sa hodí, ale určite stojí zato.', 'bauhaus/wassily-chair-1925-marcel-breuer.jpg', 342, 1925, 60, 120, 60),
        ('Kuchynská skrinka', 'Ako vytrhnuté z bežnej domácnosti. Vedľa piecky stála vždy jedna tákato vševyužiteľná skrinka. Odložíte do nej riad, aj iné potreby do kuchyne.', 'middle-ages/cabinet.jpg', 460, 1630, 150, 200, 50),
        ('Otomanská vyšívaná taburetka', 'Kus ručnej práce z blízkeho východu. Turecký skvost s detalným vzorom', 'middle-ages/ottoman-marquetry.jpg', 980, 1411, 40, 50, 40),
        ('Hodnoverná dedinská stredoveká latrína', 'Na záchod v spartanských podmienkach s dobovým prírodným nádychom', 'middle-ages/toilet.jpg', 720, 1600, 120, 200, 70),
        ('Panovnícky stolec replika', 'Vážne kráľovské posedenie, keď potrebujete pôsobiť dôležito alebo len dopĺňate zbierku okázlivých dekorácií', 'middle-ages/wood-leather-antique-chair.jpg', 1900, 1352, 50, 180, 60),
        ('Zlatistá rokoková pohovka', 'Pohovka s nádhernou zlatou tkaninou. Bliští sa až za roh a vykúzli slnečný svit v miestnosti, kde bude postávať.', 'roccoco/bench-1787.jpg', 3720, 1787, 250, 150, 60),
        ('Ozdobný stôl do honosného sídla', 'Pre hostenie vzácnych návštev s vkusom pre ozdobné prvky a zároveň baživý po malej jednohubke alebo kúsku ovocia je to ideálne spojenie.', 'roccoco/console-table-1710.jpg', 3115, 1710, 70, 80, 40),
        ('Jedálenská rokoková stolička', 'Z istého pohľadu bežná stolička. V rokoku všetko pôsobí primerane pompézne.', 'roccoco/chairs-1731.jpg', 2845, 1731, 60, 110, 45),
        ('Pracovná stolička s mäkkým sedákom', 'Drevená stolička z trocha nižšej triedy ale predsa len kvalitnou výrobou. Vyznačuje sa pohodlným sedákom, kde vydržíte hodiny bez nepríjemných pocitov.', 'roccoco/wood-antique-chair.jpg', 1050, 1740, 50, 125, 45);


